// Definitions: https://github.com/DefinitelyTyped/DefinitelyTyped
// TypeScript Version: 2.2

type KeyType = string;
type ValueType = any;
type RawDict = object;

export interface Caseless {
    set(houseName: KeyType, value: ValueType, clobber?: boolean): KeyType | false;
    set(dict: RawDict): void;
    has(houseName: KeyType): KeyType | false;
    get(houseName: KeyType): ValueType | undefined;
    swap(houseName: KeyType): void;
    del(houseName: KeyType): boolean;
}

export interface Httpified {
  headers: RawDict;
  setHeader(houseName: KeyType, value: ValueType, clobber?: boolean): KeyType | false;
  setHeader(dict: RawDict): void;
  hasHeader(houseName: KeyType): KeyType | false;
  getHeader(houseName: KeyType): ValueType | undefined;
  removeHeader(houseName: KeyType): boolean;
}

export function httpify(resp: object, headers: RawDict): Caseless;

declare function caseless(dict?: RawDict): Caseless;

export default caseless;
